mapper.select:
SELECT COUNT(*) AS count,
DATE_FORMAT(day_time, '%Y-%m-%d') AS day
FROM clock_log
WHERE start_time >= '${@com.yu.config.WorkTimeConfig@startTime}'
and day_time between #{start} and #{end}
GROUP BY day_time;

select log.id,log.a_type,log.create_time,u.username as name,u.mobile as phone from access_log log left join sys_user u on log.user_id = u.id where (a_type = 2 or a_type = 3) and log.deleted = 0
and log.create_time between #{q.startTime} and #{q.endTime}
order by create_time desc
